mapper.select:
SELECT  MACRO_CODE,MACRO_NAME,MACRO_TYPE
FROM T_CODE_MACRO_DEFINE
WHERE STATUS = 'M0043' AND PARENT_CODE = #{parentCode}
AND DEL_FLAG = 'M0015'
ORDER BY MACRO_NO;

SELECT  MACRO_CODE,MACRO_NAME
FROM T_CODE_MACRO_DEFINE
WHERE STATUS = 'M0043'
AND MACRO_TYPE = #{macroType}
AND DEL_FLAG = 'M0015'
ORDER BY MACRO_NO;
